(9, 1, '2023-07-01 13:55:30', '2023-07-01 13:55:30', '<!-- wp:group {\"align\":\"wide\"} -->\n<div class=\"wp-block-group alignwide\"><!-- wp:heading {\"textAlign\":\"center\",\"style\":{\"typography\":{\"fontSize\":\"30px\",\"fontStyle\":\"normal\",\"fontWeight\":\"800\"}}} -->\n<h2 class=\"wp-block-heading has-text-align-center\" style=\"font-size:30px;font-style:normal;font-weight:800\">TAMAN BACA \"IBRAHIM SATTAH\"</h2>\n<!-- /wp:heading -->\n\n<!-- wp:heading {\"textAlign\":\"center\",\"level\":4,\"align\":\"wide\",\"style\":{\"typography\":{\"fontStyle\":\"normal\",\"fontWeight\":\"500\"}}} -->\n<h4 class=\"wp-block-heading alignwide has-text-align-center\" style=\"font-style:normal;font-weight:500\">Taman baca atau biasa dikenal dengan perpustakaan merupakan salah satu fasilitas yang disediakan oleh SMA Cendana Pekanbaru yang menjadi salah satu ikonik dari SMA Cendana Pekanbaru. Perpustakaan tersebut diberi nama \"Ibrahim Sattah\". Perpustakaan Ibrahim Sattah menjadi salah satu ikonik dari SMA Cendana Pekanbaru dikarenakan prestasi yang telah diraih sangat luar biasa, dimana perpustakaan Ibrahim Sattah menjadi perpustakaan terbaik ke-3 se-Nasional. Selain itu Perpustakaan Ibrahim Sattah telah menyandang akreditasi A, hal ini membuktikan bahwa kualitas perpustakaan tersebut sangat baik.</h4>\n<!-- /wp:heading --></div>\n<!-- /wp:group -->\n\n<!-- wp:columns {\"align\":\"wide\"} -->\n<div class=\"wp-block-columns alignwide\"><!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:group -->\n<div class=\"wp-block-group\"><!-- wp:image {\"align\":\"full\",\"id\":37,\"sizeSlug\":\"full\"} -->\n<figure class=\"wp-block-image alignfull size-full\"><img src=\"http://cendana-alb-1657713023.us-west-2.elb.amazonaws.com/wp-content/themes/twentytwenty/assets/images/2020-three-quarters-1.png\" alt=\"\" class=\"wp-image-37\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:heading {\"level\":3} -->\n<h3 class=\"wp-block-heading\">Kerja dan Hari-hari</h3>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>1 Agustus -- 1 Desember</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:button {\"className\":\"is-style-outline\"} -->\n<div class=\"wp-block-button is-style-outline\"><a class=\"wp-block-button__link wp-element-button\" href=\"https://make.wordpress.org/core/2019/09/27/block-editor-theme-related-updates-in-wordpress-5-3/\">Baca Selengkapnya</a></div>\n<!-- /wp:button --></div>\n<!-- /wp:group -->\n\n<!-- wp:group -->\n<div class=\"wp-block-group\"><!-- wp:image {\"align\":\"full\",\"id\":37,\"sizeSlug\":\"full\"} -->\n<figure class=\"wp-block-image alignfull size-full\"><img src=\"http://cendana-alb-1657713023.us-west-2.elb.amazonaws.com/wp-content/themes/twentytwenty/assets/images/2020-three-quarters-3.png\" alt=\"\" class=\"wp-image-37\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:heading {\"level\":3} -->\n<h3 class=\"wp-block-heading\">Teater Operasi</h3>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>1 Oktober -- 1 Desember</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:button {\"className\":\"is-style-outline\"} -->\n<div class=\"wp-block-button is-style-outline\"><a class=\"wp-block-button__link wp-element-button\" href=\"https://make.wordpress.org/core/2019/09/27/block-editor-theme-related-updates-in-wordpress-5-3/\">Baca Selengkapnya</a></div>\n<!-- /wp:button --></div>\n<!-- /wp:group --></div>\n<!-- /wp:column -->\n\n<!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:group -->\n<div class=\"wp-block-group\"><!-- wp:image {\"align\":\"full\",\"id\":37,\"sizeSlug\":\"full\"} -->\n<figure class=\"wp-block-image alignfull size-full\"><img src=\"http://cendana-alb-1657713023.us-west-2.elb.amazonaws.com/wp-content/themes/twentytwenty/assets/images/2020-three-quarters-2.png\" alt=\"\" class=\"wp-image-37\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:heading {\"level\":3} -->\n<h3 class=\"wp-block-heading\">Kehidupan yang Pantas Saya Terima</h3>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>1 Agustus -- 1 Desember</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:button {\"className\":\"is-style-outline\"} -->\n<div class=\"wp-block-button is-style-outline\"><a class=\"wp-block-button__link wp-element-button\" href=\"https://make.wordpress.org/core/2019/09/27/block-editor-theme-related-updates-in-wordpress-5-3/\">Baca Selengkapnya</a></div>\n<!-- /wp:button --></div>\n<!-- /wp:group -->\n\n<!-- wp:group -->\n<div class=\"wp-block-group\"><!-- wp:image {\"align\":\"full\",\"id\":37,\"sizeSlug\":\"full\"} -->\n<figure class=\"wp-block-image alignfull size-full\"><img src=\"http://cendana-alb-1657713023.us-west-2.elb.amazonaws.com/wp-content/themes/twentytwenty/assets/images/2020-three-quarters-4.png\" alt=\"\" class=\"wp-image-37\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:heading {\"level\":3} -->\n<h3 class=\"wp-block-heading\">Dari Signac sampai Matisse</h3>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>1 Oktober -- 1 Desember</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:button {\"className\":\"is-style-outline\"} -->\n<div class=\"wp-block-button is-style-outline\"><a class=\"wp-block-button__link wp-element-button\" href=\"https://make.wordpress.org/core/2019/09/27/block-editor-theme-related-updates-in-wordpress-5-3/\">Baca Selengkapnya</a></div>\n<!-- /wp:button --></div>\n<!-- /wp:group --></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns -->\n\n<!-- wp:image {\"align\":\"full\",\"id\":37,\"sizeSlug\":\"full\"} -->\n<figure class=\"wp-block-image alignfull size-full\"><img src=\"http://cendana-alb-1657713023.us-west-2.elb.amazonaws.com/wp-content/themes/twentytwenty/assets/images/2020-landscape-2.png\" alt=\"\" class=\"wp-image-37\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:group {\"align\":\"wide\"} -->\n<div class=\"wp-block-group alignwide\"><!-- wp:heading {\"textAlign\":\"center\",\"textColor\":\"accent\"} -->\n<h2 class=\"wp-block-heading has-text-align-center has-accent-color has-text-color\">“Cyborg, sebagaimana didirikan oleh filsuf Donna Haraway, tidak dihormati. Mereka tidak ingat akan kosmos.”</h2>\n<!-- /wp:heading --></div>\n<!-- /wp:group -->\n\n<!-- wp:paragraph {\"dropCap\":true} -->\n<p class=\"has-drop-cap\">Dengan arsitektur tujuh lantai yang megah, UMoMA melaksanakan pameran seni kontemporer internasional, terkadang bersamaan dengan retrospektif sejarah seni. Isu-isu faktual, politik dan filosofis adalah muatan dasar dari acara kami. Sebagai pengunjung, Anda diajak untuk menyimak diskusi dengan seniman, ceramah, pemutaran film dan kegiatan lainnya tanpa biaya masuk. </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Pameran diadakan oleh UMoMA bekerjasama dengan seniman dan museum di seluruh dunia dan mereka selalu menarik perhatian internasional. UMoMA menerima penghargaan Special Commendation from the European Museum of the Year di antara kandidat-kandidat papan atas untuk Swedish Museum of the Year Award serta Council of Europe Museum Prize. </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:group {\"align\":\"wide\",\"style\":{\"color\":{\"background\":\"#ffffff\"}}} -->\n<div class=\"wp-block-group alignwide has-background\" style=\"background-color:#ffffff\"><!-- wp:group -->\n<div class=\"wp-block-group\"><!-- wp:heading {\"textAlign\":\"center\"} -->\n<h2 class=\"wp-block-heading has-text-align-center\">Jadilah Anggota dan Dapatkan Penawaran Khusus!</h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph {\"align\":\"center\"} -->\n<p class=\"has-text-align-center\">Anggota mendapatkan akses untuk pameran dan penjualan eksklusif. Biaya keanggotaan kami $99.99 dan ditagih setahun sekali. </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:button {\"className\":\"aligncenter\"} -->\n<div class=\"wp-block-button aligncenter\"><a class=\"wp-block-button__link wp-element-button\" href=\"https://make.wordpress.org/core/2019/09/27/block-editor-theme-related-updates-in-wordpress-5-3/\">Bergabung dengan Kami</a></div>\n<!-- /wp:button --></div>\n<!-- /wp:group --></div>\n<!-- /wp:group -->\n\n<!-- wp:gallery {\"linkTo\":\"none\",\"align\":\"wide\"} -->\n<figure class=\"wp-block-gallery alignwide has-nested-images columns-default is-cropped\"><!-- wp:image {\"id\":39,\"sizeSlug\":\"large\",\"linkDestination\":\"none\"} -->\n<figure class=\"wp-block-image size-large\"><img src=\"http://cendana-alb-1657713023.us-west-2.elb.amazonaws.com/wp-content/themes/twentytwenty/assets/images/2020-square-2.png\" alt=\"\" class=\"wp-image-39\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:image {\"id\":38,\"sizeSlug\":\"large\",\"linkDestination\":\"none\"} -->\n<figure class=\"wp-block-image size-large\"><img src=\"http://cendana-alb-1657713023.us-west-2.elb.amazonaws.com/wp-content/themes/twentytwenty/assets/images/2020-square-1.png\" alt=\"\" class=\"wp-image-38\"/></figure>\n<!-- /wp:image --></figure>\n<!-- /wp:gallery -->', 'Representatif SMA Cendana Pekanbaru', '', 'publish', 'closed', 'closed', '', 'umoma-baru-membuka-pintunya', '', '', '2023-07-01 14:57:48', '2023-07-01 14:57:48', '', 0, 'http://cendana-alb-1657713023.us-west-2.elb.amazonaws.com/?page_id=9', 0, 'page', '', 0),
